mapper.update:
update singer
set singer_album=#{album_Name}
where singer_name = #{singer_Name}

mapper.select:
select singer_ID
from singer
where singer_ID = #{id}

select singer_ID, user_ID, singer_name, singer_avatar
from singer
where singer_ID = #{singer_ID}

select singer_ID, singer_name, singer_avatar
from singer
where user_ID = #{user_ID}
