mapper.select:
select comment, user_name, postingTime,user_avatar
from songcomment,
     user
where playlist_ID = #{ID}
  and creator = user.user_ID

select comment, user_name, postingTime, user_avatar
from songcomment,
     user
where song_ID = #{ID}
  and creator = user.user_ID

select comment, user_name, postingTime,user_avatar
from songcomment,
     user
where album_ID = #{ID}
  and creator = user.user_ID
